<style lang="scss">
    @use "../../../variables.scss" as *;
    svg {
        .text-group {
            fill: url(#logo-gradient);
            filter: blur(40px);
            stroke: none;
            > * {
                transform: scale(1.5);
                // animation: flood-background 4s ease-out;
                transform-origin: center;
            }
        }
        .outlines {
            fill: $background-color;
            // animation: outline-fade-out 4s ease-out;
            // opacity: 0;
        }
        path {
            paint-order: stroke;
            stroke-linecap: square;
        }
        .canvasDraw {
            display: none;
        }
    }
    .main-path {
        animation: main-dash 3s ease-out;
        stroke-dasharray: 3900 0;
        // stroke: #000;
        stroke-width: 4px;
        paint-order: fill stroke;
    }

    @keyframes flood-background {
        from {
            transform: scale(0);
        }
        to {
            transform: scale(1);
        }
    }

    @keyframes main-dash {
        from {
            stroke-dashoffset: 3900;
            stroke-dasharray: 3900 3900;
            // stroke-dasharray: 1000;
        }
        to {
            stroke-dashoffset: calc(3900 / 2);
            stroke-dasharray: 3900 0;
            // stroke-dasharray: 3900;
        }
    }
    @keyframes delayBackupTextAppear {
        0% {
            opacity: 0;
        }
        80% {
            opacity: 0;
        }
        to {
            opacity: 1;
        }
    }
    @keyframes outline-fade-out {
        0% {
            opacity: 1;
        }
        60% {
            opacity: 1;
        }
        to {
            opacity: 0;
        }
    }
    @keyframes blur-appear {
        0% {
            opacity: 0;
        }
        100% {
            opacity: 1;
        }
    }

    canvas {
        transform-origin: 20% 20%;
        animation: blur-appear 2s ease-out;

        // filter: invert(100%) hue-rotate(180deg);
        // mix-blend-mode: difference;
    }

    .backupTitle {
        position: absolute;
        bottom: 0;
        top: 0;
        z-index: 1;

        font-size: 15rem;
        // Includes a time delay incase the canvas doesn't render
        animation: delayBackupTextAppear 4s ease-out;
        .warning {
            font-size: 4rem;
            color: #ee4444;
        }
    }
    .overflow-container {
        overflow: hidden;
        position: absolute;
        top: 0;
        left: 0;
        width: 100vw;
        height: 100vh;
        z-index: -1;

    }
</style>
